package frontweb.homework_vo;

public class Emp4 {
	String job;
	double avg;
	public Emp4(String job, double avg) {
		this.job = job;
		this.avg = avg;
	}
	public Emp4() {
		// TODO Auto-generated constructor stub
	}
	public String getJob() {
		return job;
	}
	public void setJob(String job) {
		this.job = job;
	}
	public double getAvg() {
		return avg;
	}
	public void setAvg(double avg) {
		this.avg = avg;
	}
}
