package javaexp.z02_homework.a21_hcj.h1018;

public class Member {
	private String id;
	private String pass;
	private String name;
	private boolean adm;
	private int Point;
	public Member() {
		// TODO Auto-generated constructor stub
	}
	public Member(String id, String pass, String name, boolean adm, int point) {
		this.id = id;
		this.pass = pass;
		this.name = name;
		this.adm = adm;
		Point = point;
	}
	public String getId() {
		return id;
	}
	public void setId(String id) {
		this.id = id;
	}
	public String getPass() {
		return pass;
	}
	public void setPass(String pass) {
		this.pass = pass;
	}
	public String getName() {
		return name;
	}
	public void setName(String name) {
		this.name = name;
	}
	public boolean isAdm() {
		return adm;
	}
	public void setAdm(boolean adm) {
		this.adm = adm;
	}
	public int getPoint() {
		return Point;
	}
	public void setPoint(int point) {
		Point = point;
	}
	
}
