package javaexp.a06_objectreview.vo;

public class Subject {
	private String name;
	private int point;
	public Subject() {
		// TODO Auto-generated constructor stub
	}
	public Subject(String name, int point) {
		this.name = name;
		this.point = point;
	}
	public String getName() {
		return name;
	}
	public void setName(String name) {
		this.name = name;
	}
	public int getPoint() {
		return point;
	}
	public void setPoint(int point) {
		this.point = point;
	}
	
}
