public class Miniprogramming2 {

   public static void main(String[] args) {
      // TODO Auto-generated method stub

            
            // <두 주사위의 결과값이 같을 때 이기는 게임>
            
            System.out.println("# 두 주사위의 결과값이 같을 때 이기는 게임 #");
            int team01 = (int)(Math.random()*6+1);
            int team02 = (int)(Math.random()*6+1);
            System.out.println("첫번째 주사위 수:"+team01);
            System.out.println("두번쨰 주사위 수:"+team02);
            if(team01==team02) {
               System.out.println("결과 : 플레이어 승리");
            }             if(team01>team02) {
               System.out.println("결과 : 컴퓨터 승리");
            }            if(team01<team02) {
               System.out.println("결과 : 컴퓨터 승리");
            }
                  
            
         }

      }