package javaexp.z02_homework.a11_mhi.vo;

public class StudentSsangYong {
	//쌍용 학원의 학생 정보 및 성적
	private String name;
	private int age;
	private int grades; //성적
//	public StudentSsangYong() {
//		// TODO Auto-generated constructor stub
//	}
	public StudentSsangYong(String name, int age, int grades) {
		this.name = name;
		this.age = age;
		this.grades = grades;
	}
	
	public void studentInfo() {
		System.out.println("# Student 객체의 정보 #");
		System.out.println("이름:"+name);
		System.out.println("나이:"+age);
		System.out.println("성적:"+grades);
	}

	public String getName() {
		return name;
	}

	public void setName(String name) {
		this.name = name;
	}

	public int getAge() {
		return age;
	}

	public void setAge(int age) {
		this.age = age;
	}

	public int getGrades() {
		return grades;
	}

	public void setGrades(int grades) {
		this.grades = grades;
	}
	
	
	
	
	
	
}
